import React, { Component } from "react";
import axios from "axios";
import { Card, Header, Form, Input, Icon, Button } from "semantic-ui-react";

let endpoint = "http://localhost:8080";

class ToDoList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      task: "",
      items: [],
    };
  }

  componentDidMount() {
    this.getTask();
  }

  onChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  onSubmit = () => {
    let { task } = this.state;
    // console.log("pRINTING task", this.state.task);
    if (task) {
      axios
        .post(
          endpoint + "/api/task",
          {
            task,
          },
          {
            headers: {
              "Content-Type": "application/x-www-form-urlencoded",
            },
          }
        )
        .then((res) => {
          this.getTask();
          this.setState({
            task: "",
          });
          console.log(res);
        });
    }
  };

  getTask = () => {
    axios.get(endpoint + "/api/task").then((res) => {
      if (res.data) {
        this.setState({
          items: res.data.map((item) => {
            let style = {
              wordWrap: "break-word",
              border: "solid #ffc40c",
              width: "100%",
              margin: "4px",
              justifycontent: "center",
              borderRadius: "10px",
            };

            if (item.status) {
              style["border"] = "solid green";
              style["textDecorationLine"] = "line-through";
            }

            return (
              <div
                key={item._id}
                style={style}
                className="flex justify-center w-full"
              >
                <div className="relative justify-center">
                  <div className="flex flex-col justify-center text-center">
                    {item.task}
                    <Card.Meta textAlign="center" class="content">
                      <Button
                        size="small"
                        className="ui color1 button"
                        onClick={() => this.updateTask(item._id)}
                      >
                        Done
                      </Button>
                      <Button
                        size="small"
                        className="ui color2 button"
                        onClick={() => this.undoTask(item._id)}
                      >
                        Undo
                      </Button>
                      <Button
                        size="small"
                        className="ui color3 button"
                        onClick={() => this.deleteTask(item._id)}
                      >
                        Delete
                      </Button>
                    </Card.Meta>
                  </div>
                </div>
              </div>
            );
          }),
        });
      } else {
        this.setState({
          items: [],
        });
      }
    });
  };

  updateTask = (id) => {
    axios
      .put(endpoint + "/api/task/" + id, {
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
      })
      .then((res) => {
        console.log(res);
        this.getTask();
      });
  };

  undoTask = (id) => {
    axios
      .put(endpoint + "/api/undoTask/" + id, {
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
      })
      .then((res) => {
        console.log(res);
        this.getTask();
      });
  };

  deleteTask = (id) => {
    axios
      .delete(endpoint + "/api/deleteTask/" + id, {
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
      })
      .then((res) => {
        console.log(res);
        this.getTask();
      });
  };

  render() {
    return (
      <div className="flex flex-col justify-center p-4 w-full ">
        <div className="flex justify-center m-4 text-3xl font-bold text-purple-600">
          TO DO LIST
        </div>
        <div className="relative ">
          <Form onSubmit={this.onSubmit} class="ui form">
            <Input
              type="text"
              name="task"
              onChange={this.onChange}
              value={this.state.task}
              fluid
              placeholder="Create Task"
            />
            <div className="flex justify-center m-4">
              <Button>Create Task</Button>
              </div>
          </Form>
        </div>
        <div className="flex flex-row justify-center w-full">
          <div className="relative w-full">{this.state.items}</div>
        </div>
      </div>
    );
  }
}

export default ToDoList;
